import React, { useState, useEffect } from "react";
import axios from "axios";

function RegisterAcion() {
  const [cedula, setCedula] = useState("");
  const [accion, setAccion] = useState("");
  const [mensaje, setMensaje] = useState("");
  const [fechaHora, setFechaHora] = useState(new Date());
  const [loading, setLoading] = useState(false);
  const [registrado, setRegistrado] = useState(false);

  // ‚è∞ Actualizar hora cada segundo
  useEffect(() => {
    const timer = setInterval(() => setFechaHora(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);


  // üßæ Manejar env√≠o del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!cedula.trim()) {
      setMensaje("‚ö†Ô∏è Ingresa una c√©dula v√°lida");
      return;
    }

    if (!accion) {
      setMensaje("‚ö†Ô∏è Selecciona una acci√≥n");
      return;
    }

    setMensaje("‚è≥ Registrando...");
    setLoading(true);

    try {
      // Simular solicitud al servidor
      // const res = await axios.post("http://localhost:3000/api/registro", { cedula, accion });
      await new Promise((resolve) => setTimeout(resolve, 1000));

      const cedulasRegistradas = ["12345", "67890"];
      if (!cedulasRegistradas.includes(cedula)) {
        setMensaje("üÜï C√©dula no encontrada, agregada exitosamente ‚úÖ");
      } else {
        setMensaje("‚úÖ Registro exitoso");
      }

      setRegistrado(true);
    } catch (error) {
      console.error(error);
      setMensaje("‚ùå Error al conectar con el servidor");
    } finally {
      setLoading(false);
    }
  };
  // üö™ Funci√≥n para cerrar sesi√≥n
  const handleLogout = async () => {
    try {
      // Llama a tu backend (ajusta la URL seg√∫n tu servidor)
      await axios.post("http://localhost:3000/api/logout", {}, { withCredentials: true });

      // Limpia almacenamiento local (si usas JWT o sesi√≥n)
      localStorage.removeItem("token");
      sessionStorage.clear();

      // Redirige al login
      window.location.href = "/";
    } catch (error) {
      console.error("Error al cerrar sesi√≥n:", error);
      setMensaje("‚ùå Error al cerrar sesi√≥n");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-100 to-gray-200">
      <div className="bg-white/90 backdrop-blur-xl p-8 rounded-2xl shadow-xl w-full max-w-md border border-gray-200">

        {/* üî¥ Bot√≥n de Cerrar sesi√≥n (va aqu√≠ arriba del t√≠tulo) */}
        <div className="flex justify-end mb-4">
          <button
            onClick={handleLogout}
            className="text-sm bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-lg transition"
          >
            Cerrar sesi√≥n
          </button>
        </div>

        <h1 className="text-2xl font-bold mb-6 text-center text-blue-600">
          Registrar Acci√≥n
        </h1>

        <form onSubmit={handleSubmit} className="space-y-4">
          {/* C√©dula */}
          <div>
            <label className="block text-gray-700 font-medium">C√©dula</label>
            <input
              type="text"
              value={cedula}
              onChange={(e) => setCedula(e.target.value)}
              required
              className="w-full p-2 border rounded-lg mt-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="Ingrese su c√©dula"
              disabled={loading}
            />
          </div>

          {/* Acci√≥n */}
          <div>
            <label className="block text-gray-700 font-medium">Acci√≥n</label>
            <select
              value={accion}
              onChange={(e) => setAccion(e.target.value)}
              required
              className="w-full p-2 border rounded-lg mt-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
              disabled={loading}
            >
              <option value="">Seleccionar acci√≥n</option>
              <option value="ingreso">Ingreso</option>
              <option value="ingreso_almuerzo">Ingreso almuerzo</option>
              <option value="regreso_almuerzo">Regreso almuerzo</option>
              <option value="salida">Salida</option>
            </select>
          </div>

          {/* Fecha y hora actual */}
          <div className="text-center text-gray-600 text-sm font-medium">
            {fechaHora.toLocaleString()}
          </div>

          {/* Bot√≥n */}
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-2 rounded-lg font-semibold transition ${
              loading
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-blue-600 hover:bg-blue-700 text-white"
            }`}
          >
            {loading ? "Registrando..." : "Registrar"}
          </button>
        </form>

        {/* Mensaje de estado */}
        {mensaje && (
          <div
            className={`mt-4 text-center font-medium ${
              mensaje.includes("‚úÖ") || mensaje.includes("üÜï")
                ? "text-green-600"
                : mensaje.includes("‚ùå")
                ? "text-red-600"
                : "text-gray-700"
            }`}
          >
            {mensaje}
          </div>
        )}
      </div>
    </div>
  );
}

export default RegisterAcion;
