import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import DataTable from "../components/DataTable";

const registros = [
  { nombre: "Juan Pérez", cedula: "12345", accion: "Ingreso", fecha: "2025-10-22 08:00" },
  { nombre: "María López", cedula: "67890", accion: "Salida", fecha: "2025-10-22 17:00" },
];

function AsistenciasPage() {
  const [filtro, setFiltro] = useState("");

  const filtrados = registros.filter((r) =>
    r.nombre.toLowerCase().includes(filtro.toLowerCase())
  );

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <main className="flex-1 p-6">
        <h1 className="text-2xl font-semibold text-gray-800 mb-6">
          Gestión de Asistencias
        </h1>

        <div className="mb-4 flex gap-2">
          <input
            type="text"
            placeholder="Buscar por nombre o cédula"
            value={filtro}
            onChange={(e) => setFiltro(e.target.value)}
            className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-gray-500"
          />
          <button className="bg-gray-900 text-white px-4 rounded-lg hover:bg-gray-800 transition">
            Exportar CSV
          </button>
        </div>

        <DataTable
          data={filtrados}
          columns={[
            { label: "Nombre", accessor: "nombre" },
            { label: "Cédula", accessor: "cedula" },
            { label: "Acción", accessor: "accion" },
            { label: "Fecha", accessor: "fecha" },
          ]}
        />
      </main>
    </div>
  );
}

export default AsistenciasPage;
